mapper.select:
WHERE commodity.state_id = 3
and commodity.view_num <= (SELECT view_num FROM t_commoditys WHERE id = #{lastCommId} )
ORDER BY commodity.view_num DESC LIMIT #{maxInfoSize}

WHERE commodity.state_id = 3
and commodity.buy_current_number-commodity.buy_last_number >= (SELECT t_commoditys.buy_current_number-t_commoditys.buy_last_number FROM t_commoditys WHERE t_commoditys.id = #{lastCommId} )
ORDER BY commodity.buy_current_number-commodity.buy_last_number limit #{maxInfoSize}

WHERE commodity.state_id = 3
and commodity.ground_time >= (SELECT t_commoditys.ground_time FROM t_commoditys WHERE t_commoditys.id = (SELECT temp_id FROM t_commoditys WHERE id=#{lastCommId}) )
ORDER BY ground_time DESC limit #{maxInfoSize}

WHERE commodity.state_id = 3
and template.buy_total_number <= (SELECT buy_total_number FROM t_commodity_template WHERE id=(SELECT temp_id FROM t_commoditys WHERE id=#{lastCommId})
ORDER BY template.buy_total_number DESC limit #{maxInfoSize}


WHERE commodity.state_id = 3
and template.buy_total_number >
(SELECT t_commodity_template.buy_total_number FROM t_commodity_template
WHERE t_commodity_template.id =(SELECT temp_id FROM t_commoditys WHERE id=#{lastCommId}) )
AND template.buy_total_number < #{number}
limit #{maxInfoSize}


WHERE template.commodity_type_id = #{typeId}
and template.name like #{name}
and commodity.state_id = #{stateId}
and commodity.id > #{lastCommId}
ORDER BY
commodity.id limit #{pageLoadSize}


WHERE template.name like #{name}
and commodity.state_id = #{stateId}
and commodity.id > #{lastCommId}
order by commodity.id limit #{pageLoadSize}


WHERE template.commodity_type_id = #{typeId}
and commodity.state_id = #{stateId}
and commodity.id > #{lastCommId}
order by commodity.id limit #{pageLoadSize}

SELECT
    id,
    round_time
FROM t_commoditys
WHERE temp_id = #{tempId}
      AND state_id = #{stateId}

mapper.update:
UPDATE t_commoditys
SET state_id = #{stateId}
WHERE id = #{commId}
