mapper.update:
update draw_lottery.t_commoditys SET state_id = #{state},

    ground_time = #{groundTime},

undercarriage_time = #{underTime}
where id in

    #{id}

mapper.select:




        temp.name like #{pattern}


        and temp.genre in

            #{genre}



        and temp.commodity_type_id in

            #{type}



        and temp.state_id in

            #{state}



        and temp.ground_time > #{groundTimeFront}
        and temp.ground_time < #{groundTimeAfter}


        and temp.undercarriage_date > #{undercarriageTimeFront}
        and temp.undercarriage_date < #{undercarriageTimeAfter}

    and temp.valid = #{valid}

order BY


        temp.ground_time


        temp.undercarriage_date


        temp.buyTotalNumber


        commodity.buyCurrentNumber


        buyNowNumber


        viewNum


        commodity.id




        DESC


        ASC
